mapper.insert:
INSERT INTO gates (gate_code, airport_code, terminal_code)
VALUES (
#{gateObj.gateCode},
#{gateObj.airportCode},
#{gateObj.terminalCode}
)

mapper.select:
SELECT * FROM gates
WHERE gate_code = #{gateCode}
AND airport_code = #{airportCode}
AND terminal_code = #{terminalCode}

mapper.update:
UPDATE gates
SET
gate_code = #{gateObj.gateCode},
airport_code = #{gateObj.airportCode},
terminal_code = #{gateObj.terminalCode}
WHERE gate_id = #{gateObj.gateId}

mapper.delete:
DELETE FROM gates
WHERE gate_code = #{gateCode}
AND airport_code = #{airportCode}
AND terminal_code = #{terminalCode}

mapper.select:
SELECT COUNT(*) > 0 FROM gates
WHERE gate_code = #{gateCode}
AND airport_code = #{airportCode}

SELECT f.*
FROM flights f
WHERE f.gate_id = #{gateId}
