mapper.select:
SELECT * FROM terminals
WHERE
airport_code = #{airportCode}
AND terminal_code = #{terminalCode}

mapper.delete:
DELETE FROM terminals
WHERE
airport_code = #{airportCode}
AND terminal_code = #{terminalCode}

mapper.select:
SELECT g.*
FROM gates g
WHERE g.airport_code = #{airportCode}
AND g.terminal_code = #{terminalCode}
